import styled, {css} from 'styled-components';
import {Link} from 'react-router-dom';

//css is used to be nested 
const OptionContainerStyles = css`
    padding: 10px 15px;
    cursor: pointer;
    justify-content: space-between;
`

export const HeaderContainer = styled.div`
    height: 70px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin-bottom: 25px;
`;

export const LogoContainer = styled(Link)`
    height: 100%;
    width: 70px;
    padding: 20px 60px; 
    padding: 25px;
`; 

export const OptionsContainer = styled(Link)`
      width: 50%; //always takes up to only 50% of whatever header has (parent component)
      height: 100%;
      display: flex;
      align-items: center; 
      justify-content: flex-end; //options stay at the most right side 
`;

export const OptionLink = styled(Link)`
   ${OptionContainerStyles}
`
export const OptionDiv = styled.div`
  ${OptionContainerStyles}
`;
