import React from 'react';
import { withRouter } from 'react-router-dom';
// import {
//   MenuItemContainer,
//   BackgroundImageContainer,
//   ContentContainer,
//   ContentTitle,
//   ContentSubtitle
// } from './menu-item.styles';
import "./menu-item.styles.scss";

const temp_pics_sources = [
    require('../../../assets/Menu1.jpg'),
    require('../../../assets/Menu2.jpg'),
    require('../../../assets/Menu3.jpg'),
    require('../../../assets/Menu4.jpg'),
    require('../../../assets/Menu5.jpg')
]; 

//functional component 
//since we don't really have many things to hold inside of the component 

//we want to have the logic 
//to dynamically generate the title into the menu component


//        style={{backgroundImage: `url(${temp_pics_sources[id-1]})`}}

const MenuItem = ({id, title, imageUrl, size, history, linkUrl, match}) => {
 
   
   console.log("hey",temp_pics_sources[id-1]);
 
   return(
     <div
       className="menu-item"
       style={{backgroundColor: "orange", backgroundImage: "url(`${temp_pics_sources[id-1}`)" }}
       size={size}
       onClick={() => history.push(`${match.url}${linkUrl}`)}>
    <div
        className="background-image"
    />
    <div className='content'>
      <span>{title.toUpperCase()}</span>
      <span>SHOP NOW</span>
    </div>
  </div>
  )
};


export default withRouter(MenuItem);
